nels, input_channels // 4) self.fc2 = nn.Linear(input_channels // 4, input_channels) self.sigmoid = nn.Sigmoid() def forward(self, x): x_avg = torch.mean(x, dim=1) # Global Average Pooling along the â€˜sequence lengthâ€™ dimension weight = F.relu(self.fc1(x_avg)) weight = self.fc2(weight) weight = self.sigmoid(weight).unsqueeze(1) out = x * weight return out class Counter_Guide_Enhanced(nn.Module): def __init__(self, input_channels, output_channels): super(Counter_Guide_Enhanced, self).__init__() self.mc = Multi_Context(input_channels, output_channels) self.cross_attention = Cross_Attention(output_channels, output_channels) # ç»“åˆè‡ªé€‚åº” self.adaptive_weight = Adaptive_Weight(output_channels) def forward(self, x, event_x): # é¦–å…ˆä½¿ç”¨Multi-Contextå¢å¼ºäº‹ä»¶ç‰¹å¾ enhanced_event_x = self.mc(event_x) # ä½¿ç”¨Cross-Attentionè¿›è¡Œç‰¹å¾äº¤äº’,(xå’Œevent_xäº’ç›¸å¢å¼º) enhanced_x = self.cross_attention(x, enhanced_event_x) enhanced_event_x_via_x = self.cross_attention(event_x, enhanced_x) # è¿›ä¸€æ­¥é€šè¿‡adaptive weightè°ƒæ•´å¢å¼ºçš„ç‰¹å¾æƒé‡ adaptive_enhanced_x = self.adaptive_weight(enhanced_x) adaptive_enhanced_event_x = self.adaptive_weight(enhanced_event_x_via_x) return adaptive_enhanced_x, adaptive_enhanced_event_x ï¼Œåœ¨ç½‘ç»œä¸­çš„é›†æˆæ–¹å¼æ˜¯ï¼š# å°† 4è¾“å…¥åˆ†å¼€ï¼Œæ„å»ºæ–°çš„ç›¸åŒæ¨¡æ€ç»“åˆçš„2è¾“å…¥ï¼Œ2åˆ†æ”¯ import math import logging from functools import partial from collections import OrderedDict from copy import deepcopy import torch import torch.nn as nn import torch.nn.functional as F from timm.models.layers import to_2tuple from lib.models.layers.patch_embed import PatchEmbed, PatchEmbed_event, xcorr_depthwise from .utils import combine_tokens, recover_tokens from .vit import VisionTransformer from ..layers.attn_blocks import CEBlock from .ad_counter_guide import Counter_Guide_Enhanced _logger = logging.getLogger(__name__) class VisionTransformerCE(VisionTransformer): \\"\\"\\" Vision Transformer with candidate elimination (CE) module A PyTorch